import { Component } from '@angular/core';
import { Stock } from 'src/app/model/stock';

@Component({
  selector: 'app-create-stock',
  templateUrl: './create-stock.component.html',
  styleUrls: ['./create-stock.component.css']
})
export class CreateStockComponent {
  public stock: Stock;

  constructor() {
    this.stock = new Stock("Test", "", 80, 85);
  }

}
<div class="mx-auto bg-gray-200">
    <h2>Create Stock Form</h2>
    <div class="form-group">
        <form>
            <div class="stock-name">
                <!-- <input type="text" placeholder="Stock Name" [value]="stock.name"> (input)="stock.name=$event.target.value"> -->
            </div>
        </form>
        <button (click)="stock.name='test'">Reset stock name</button>
    </div>
    <h4>Stock Name is {{stock.name}}</h4>
</div>
