import { AfterContentChecked, AfterContentInit, AfterViewChecked, AfterViewInit, ChangeDetectionStrategy, Component, DoCheck, EventEmitter, Input, OnChanges, OnDestroy, OnInit, Output, SimpleChanges } from '@angular/core';
import { Stock } from 'src/app/model/stock';

@Component({
  selector: 'app-stock-item',
  templateUrl: './stock-item.component.html',
  styleUrls: ['./stock-item.component.css'],
  changeDetection: ChangeDetectionStrategy.Default,
})
export class StockItemComponent implements OnInit, OnChanges, DoCheck,AfterContentChecked, AfterContentInit, AfterViewInit, AfterViewChecked, OnDestroy {
  @Input()
  public stock: any;

  @Output()
  private toggleFavorite: any;

  constructor() {
    this.toggleFavorite = new EventEmitter<Stock>();
  }

  onToggleFavorite(event: any) {
    console.log(event);
    this.toggleFavorite.emit(this.stock);
  }

  changeStockPrice() {
    this.stock.price += 5;
  }

  ngOnInit(): void {
    console.log("App Component - On Init");
  }

  ngOnChanges(changes: SimpleChanges): void {
    console.log("App Component - On Changes");
  }

  ngDoCheck(): void {
    console.log("App Component - Do Check");
  }
}
<div class="mx-auto max-w-4xl gridjustify-center items-center">
    <div class="p-3 max-w-md text-gray-900 bg-white border border-gray-200 mx-auto text-center shadow-sm rounded-md hover:shadow-lg">
        <h1 class="text-xl">{{stock.name}} </h1>
        <ul>
            <li>Code: {{stock.code}}</li>
            <li [class]="stock.isPositiveChange() ? 'text-green-600' : 'text-red-600'">Price: ${{stock.price}}</li>
        </ul>
        <div class="flex flex-row justify-center">
            <button (click)="onToggleFavorite($event)"
            *ngIf="!stock.favorite"
            class="button rounded-md bg-gray-800 hover:bg-gray-900 text-white shadow-sm p-2 m-2 hover:shadow-md" [disabled] = "stock.favorite">Add To Favorites!</button>
            <button (click)="changeStockPrice()" class="button rounded-md bg-green-800 hover:bg-green-900 text-white shadow-sm p-2 m-2 hover:shadow-md">Change Price</button>
        </div>
    </div>
</div>
