  selector: 'app-create-stock',
  templateUrl: './create-stock.component.html',
  styleUrls: ['./create-stock.component.css']
})
export class CreateStockComponent {
  public stock: Stock;

  constructor() {
    this.stock = new Stock("Test", "", 80, 85);
  }

}
